const staticData = $getWorkflowStaticData('global');

let lastSync = staticData.lastSyncTimestamp;
if (!lastSync) {
  const sevenDaysAgo = new Date();
  sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
  lastSync = sevenDaysAgo.getTime();
}

const currentRunTimestamp = Date.now();

return [{
  json: {
    lastSyncTimestamp: lastSync,
    currentRunTimestamp: currentRunTimestamp,
    lastSyncISO: new Date(lastSync).toISOString()
  }
}];

const response = $input.first().json;
const results = response.results || [];

// Get previously accumulated contacts from static data or start fresh
const staticData = $getWorkflowStaticData('global');
const previousContacts = staticData._tempAccumulatedContacts || [];
const allContacts = [...previousContacts, ...results];

const hasNextPage = response.paging && response.paging.next && response.paging.next.after;

if (hasNextPage) {
  // Store accumulated contacts and continue pagination
  staticData._tempAccumulatedContacts = allContacts;
  return [{
    json: {
      hasMorePages: true,
      paginationCursor: response.paging.next.after,
      totalSoFar: allContacts.length
    }
  }];
} else {
  // All pages fetched, clean up temp data and output all contacts
  delete staticData._tempAccumulatedContacts;

  if (allContacts.length === 0) {
    return [{ json: { hasMorePages: false, noContacts: true } }];
  }

  return [{
    json: {
      hasMorePages: false,
      totalContacts: allContacts.length,
      contacts: allContacts
    }
  }];
}

const input = $input.first().json;

if (input.noContacts) {
  return [{ json: { status: 'no_new_contacts', email: '' } }];
}

const contacts = input.contacts || [];

return contacts.map(contact => ({
  json: {
    email: (contact.properties && contact.properties.email) || '',
    first_name: (contact.properties && contact.properties.firstname) || '',
    product_list: (contact.properties && contact.properties.product_list) || '',
    lifecycle_stage: (contact.properties && contact.properties.lifecyclestage) || '',
    lead_status: (contact.properties && contact.properties.hs_lead_status) || '',
    website: (contact.properties && contact.properties.website) || '',
    dr_code: (contact.properties && contact.properties.dr_code) || '',
    country_region_code: (contact.properties && contact.properties.hs_country_region_code) || '',
    city: (contact.properties && contact.properties.city) || '',
    jf_usercat: (contact.properties && contact.properties.jf_usercat) || '',
    ist_lead_source: (contact.properties && contact.properties.ist_lead_source) || '',
    hubspot_id: contact.id || ''
  }
}));

const staticData = $getWorkflowStaticData('global');

const currentRunTimestamp = $('Get Last Sync Timestamp').first().json.currentRunTimestamp;

staticData.lastSyncTimestamp = currentRunTimestamp;

// Clean up any temp data
delete staticData._tempAccumulatedContacts;

return [{
  json: {
    status: 'sync_complete',
    lastSyncTimestamp: currentRunTimestamp,
    lastSyncISO: new Date(currentRunTimestamp).toISOString()
  }
}];
